import React, { useState } from 'react';

import { SortWrapper, SortValues } from './styles';

export const Sort = () => {
  const [sortValue, setSortValue] = useState(0);

  const changeSort = e => {
    console.log(e);
    setSortValue(e.target.value);
  };
  return (
    <SortWrapper>
      <p>Sorting</p>
      <SortValues>
        <label>
          <input
            type="radio"
            value={0}
            checked={sortValue === 0}
            onChange={changeSort}
          />
          Price High to Low
        </label>
        <label>
          <input
            type="radio"
            value={1}
            checked={sortValue === 1}
            onChange={changeSort}
          />
          Price Low to High
        </label>
        <label>
          <input
            id={2}
            type="radio"
            value={2}
            checked={sortValue === 2}
            onChange={changeSort}
          />
          New to Old
        </label>
        <label>
          <input
            type="radio"
            value={3}
            checked={sortValue === 3}
            onChange={changeSort}
          />
          Old to New
        </label>
      </SortValues>
    </SortWrapper>
  );
};
