const BASE_COLOR = '#1EA4CE';
const DARKER_COLOR = '#147594';
const WHITE = '#fff';
const BLACK = '#000';

export const BACKGROUND_COLORS = {
  DEFAULT: WHITE,
  LIGHT: BASE_COLOR,
  DARK: DARKER_COLOR,
};

export const TEXT_COLORS = {
  DEFAULT: BLACK,
  TOPBAR: WHITE,
};
