import React, { useState } from 'react';

import { SortWrapper, SortValues } from './styles';

export const Sort = () => {
  const [sortValue, setSortValue] = useState(0);
  return (
    <SortWrapper>
      <p>Sorting</p>
      <SortValues>
        <label>
          <input
            type="radio"
            value={0}
            checked={sortValue === 0}
            onChange={e => setSortValue(e.target.value)}
          />{' '}
          Price High to Low
        </label>
      </SortValues>
    </SortWrapper>
  );
};
